// =============================
//  App.js (React Frontend)
// =============================
import React, { useState } from "react";

function App() {
    // --- State ---
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [message, setMessage] = useState("");
    const [user, setUser] = useState(null);

    // --- Helper: call backend ---
    async function apiCall(endpoint, method = "GET", body = null) {
        const res = await fetch(`http://localhost:5000${endpoint}`, {
            method,
            headers: { "Content-Type": "application/json" },
            body: body ? JSON.stringify(body) : null,
            credentials: "include", // allow sessions
        });
        const data = await res.json();
        setMessage(data.message);
        return data;
    }

    // --- Register ---
    async function register(e) {
        e.preventDefault();
        await apiCall("/register", "POST", { username, password });
    }

    // --- Login ---
    async function login(e) {
        e.preventDefault();
        const result = await apiCall("/login", "POST", { username, password });
        if (result.message.startsWith("Welcome")) setUser(username);
    }

    // --- Logout ---
    async function logout() {
        await apiCall("/logout");
        setUser(null);
    }

    // --- Dashboard (check session) ---
    async function checkDashboard() {
        const result = await apiCall("/dashboard");
        if (result.message.startsWith("Welcome")) setUser(username);
    }

    // --- UI ---
    return (
        <div style={{ textAlign: "center", marginTop: "40px", fontFamily: "Arial" }}>
            <h2>ðŸ§  Simple Authentication System</h2>

            {!user ? (
                <>
                    <input
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <br />
                    <br />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    <br />
                    <br />
                    <button onClick={register}>Register</button>
                    <button onClick={login}>Login</button>
                </>
            ) : (
                <>
                    <h3>Welcome, {user}!</h3>
                    <button onClick={checkDashboard}>Check Dashboard</button>
                    <button onClick={logout}>Logout</button>
                </>
            )}

            <p
                style={{
                    color: message.includes("Welcome") ? "green" : "red",
                    marginTop: "20px",
                }}
            >
                {message}
            </p>
        </div>
    );
}

export default App;
